import React from 'react';
import io from 'socket.io-client';

class Index extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            hello: ''
        }
    }

    componentWillMount() {
        this.socket = io();
        this.socket.on('now', data => {
            this.setState({
                hello: data.message
            })
        })
    }

    render() {
        return (
            <h1>{this.state.hello}</h1>
        )
    }
}
